import React from 'react';

class PostRequest extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            code: "",
            error: null,
            isLoaded: false
        };
    }

    componentDidMount() {
        /*
        const requestOptions = {
            mode: 'no-cors',
            method: 'GET',
            headers: { 'Content-Type' : 'application/json'},
            //body: JSON.stringify({ code: "hei hva sskjer"})
        };
        */

        fetch('http://localhost:8080/docker') 
            .then(response => response.json())
            .then(
                (result) => {
                    this.setState({
                        code: result.name,
                        isLoaded: true
                    });
                },
                (error) => {
                    this.setState({
                        isLoaded: true,
                        error
                    });
                }
            );

    }

    render() {
        const { code, error, isLoaded } = this.state;
        if(error) {
            return <div>Error: {error.message}</div>
        } else if(!isLoaded) {
            return <div>Loading..</div>
        } else {
            return (
                <div>
                    The code is: {code}
                </div>
            );
        }
    }


}

export default PostRequest;
