import React from 'react';

class PostRequest extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            code: null,
            error: null,
            isLoaded: false
        };
    }

    componentDidMount() {
        const requestOptions = {
            mode: 'no-cors',
            method: 'POST',
            headers: { 'Content-Type' : 'application/json'},
            body: JSON.stringify(this.state.code)
        };

        fetch('http://localhost:8080/docker', requestOptions) 
            .then(response => response.json())
            .then(result => {
                console.log(result);
                this.setState({ code: result })
            });
                /*
                (error) => {
                    this.setState({
                        isLoaded: true,
                        error
                    });
                }
                */

    }

    render() {
        const { code, error, isLoaded } = this.state;
        if(error) {
            return <div>Error: {error.message}</div>
        } else if(!isLoaded) {
            return <div>Loading..</div>
        } else {
            return (
                <div>
                    The code is: {code}
                </div>
            );
        }
    }


}

export default PostRequest;
